mapper.select:
select
  ta.*,
  tu.name owner_name
from t_activity ta
    LEFT JOIN t_user tu ON ta.owner_id = tu.id


    AND (
      ta.name LIKE CONCAT('%', #{query.keyword}, '%')
      OR cost LIKE CONCAT('%', #{query.keyword}, '%')
      OR description LIKE CONCAT('%', #{query.keyword}, '%')
    )


    and ta.start_time >= #{query.startTime}


      and ta.end_time <= #{query.endTime}  


    AND ta.owner_id IN

      #{ownerId}


  ${ query.filterSQL }


ORDER BY ${@com.zs.crmserver.util.SqlUtils@safeSort(
    pageQuery.sortField,
    pageQuery.sortOrder,
    "create_time",
    "start_time", "end_time", "create_time", "cost", "name"
)}

mapper.delete:
delete from t_activity
where id in

  #{id,jdbcType=INTEGER}
