mapper.select:
select

from t_clue tc


    AND (
      tc.full_name LIKE CONCAT('%', #{query.keyword}, '%')
      OR tc.phone LIKE CONCAT('%', #{query.keyword}, '%')
      OR tc.weixin LIKE CONCAT('%', #{query.keyword}, '%')
      OR tc.qq LIKE CONCAT('%', #{query.keyword}, '%')
      OR tc.job LIKE CONCAT('%', #{query.keyword}, '%')
      OR tc.description LIKE CONCAT('%', #{query.keyword}, '%')
    )


    and tc.next_contact_time >= #{query.startTime}


      and tc.next_contact_time <= #{query.endTime}  


    AND tc.owner_id IN

      #{ownerId}


  ${ query.filterSQL }


ORDER BY ${@com.zs.crmserver.util.SqlUtils@safeSort(
    pageQuery.sortField,
    pageQuery.sortOrder,
    "create_time",
    "start_time", "end_time", "create_time", "cost", "name"
)}

select

FROM t_clue tc

  tc.id = #{id, jdbcType=INTEGER}

mapper.delete:
delete from t_clue
where id in

  #{id,jdbcType=INTEGER}
