mapper.select:
select tcr.* from t_clue_remark tcr
tcr.clue_id = #{clueTrackRecordQuery.clueId, jdbcType=INTEGER} and (tcr.deleted is null or tcr.deleted = 0)
AND ( tcr.note_content LIKE CONCAT('%', #{clueTrackRecordQuery.keyword}, '%') )
${ clueTrackRecordQuery.filterSQL }
ORDER BY ${@com.zs.crmserver.util.SqlUtils@safeSort( basePageQuery.sortField, basePageQuery.sortOrder, "create_time", "create_time", "edit_time" )}

mapper.update:
UPDATE t_clue_remark
SET deleted = #{deleted}
WHERE id IN

  #{id}

mapper.delete:
DELETE FROM t_clue_remark
WHERE clue_id IN

  #{id}
