mapper.select:
select

from t_customer tct
    left join t_clue tc on tct.clue_id = tc.id


    AND (
    tc.full_name LIKE CONCAT('%', #{query.keyword}, '%')
    OR tc.phone LIKE CONCAT('%', #{query.keyword}, '%')
    OR tc.weixin LIKE CONCAT('%', #{query.keyword}, '%')
    OR tc.qq LIKE CONCAT('%', #{query.keyword}, '%')
    OR tc.job LIKE CONCAT('%', #{query.keyword}, '%')
    OR tc.description LIKE CONCAT('%', #{query.keyword}, '%')
    )


    and tct.create_time >= #{query.startTime}


      and tct.create_time <= #{query.endTime}  


    AND tc.owner_id IN

      #{ownerId}


  ${ query.filterSQL }


ORDER BY ${@com.zs.crmserver.util.SqlUtils@safeSort(
    pageQuery.sortField,
    pageQuery.sortOrder,
    "create_time",
    "create_time", "edit_time"
)}
